            </b-row>
         </b-overlay>
      </div>
   </div>
</template>

<script>
import client from '@/api'
import LimpezaHorimetro from '../components/LimpezaHorimetro.vue'
import LimpezaManutencao from '../components/LimpezaManutencao.vue'
import LimpezaVibracaoDiaria from './LimpezaVibracaoDiaria.vue'
import LimpezaVibracaoMedia from './LimpezaVibracaoMedia.vue'
import LimpezaPressaoDiaria from './LimpezaPressaoDiaria.vue'
import LimpezaPressaoMedia from './LimpezaPressaoMedia.vue'
import LimpezaCompara from './LimpezaCompara.vue'

export default {
   name: 'LimpezaDados',
   components: {
      LimpezaHorimetro,
      LimpezaManutencao,
      LimpezaVibracaoDiaria,
      LimpezaVibracaoMedia,
      LimpezaPressaoDiaria,
      LimpezaPressaoMedia,
      LimpezaCompara
   },
   data() {
      return {
         buscandoDados: false,
         buscandoEquipamento: false,
         dadosLimpeza: {},
         equipamento: {},
         fullscreen: false
      }
   },

   methods: {
      async buscarDados() {
         if (!this.idLimpeza) {
            return
         }
         this.buscandoDados = true
         const dados = await client.get(`/limpeza/buscardado/${this.idLimpeza}`).catch((err) => {
            this.$bvToast.toast(`Erro ao buscar dados. ${err}`, {
               title: 'Erro',
               variant: 'warning',
               autoHideDelay: 5000,
               solid: true,
               toaster: 'b-toaster-bottom-right'
            })
            this.buscandoDados = false
         })
         this.buscandoDados = false
         if (dados && dados.data) {
            this.dadosLimpeza = dados.data
         }
      },
      async buscarEquipamento() {
         if (!this.idLimpeza) {
            return
         }
         this.buscandoEquipamento = true
         const dados = await client.get(`/equipamento/${this.idLimpeza}`).catch((err) => {
            this.$bvToast.toast(`Erro ao buscar equipamento. ${err}`, {
               title: 'Erro',
               variant: 'warning',
               autoHideDelay: 5000,
               solid: true,
               toaster: 'b-toaster-bottom-right'
            })
            this.buscandoEquipamento = false
         })
         this.buscandoEquipamento = false
         if (dados && dados.data) {
            this.equipamento = dados.data
         }
      },
      formatarData(value) {
         if (!value) return
         return this.$moment(value, 'YYYY/MM/DD HH:mm:ss').format('DD/MM/YYYY HH:mm:ss')
      },
      formatarModelo(value) {
         const modelo = {
            0: '-',
            1: 'MALT 45/60',
            2: 'MALT 90/120',
            3: 'MALT 135/180',
            4: 'MALT 180/240'
         }
         return modelo[value]
      },
      formatarOperacao(value) {
         if (this.verificarBitNaPosicao(value, 0)) {
            return { nome: 'Ligado manual', icone: 'check-square' }
         } else if (this.verificarBitNaPosicao(value, 1)) {
            return { nome: 'Ligado automático', icone: 'check-square' }
         } else if (!this.verificarBitNaPosicao(value, 0) && !this.verificarBitNaPosicao(value, 1)) {
            return { nome: 'Desligado', icone: 'dash-square' }
         } else {
            return { nome: 'Desligado', icone: 'dash-square' }
         }
      },
      verificarBitNaPosicao(binario, posicao) {
         if (!binario) return
         const posicaoInvertida = binario.length - 1 - posicao
         return binario.charAt(posicaoInvertida) === '1'
      },
      formatarStatus(value) {
         const status = {
            OF: 'Offline',
            EF: 'Alerta',
            EC: 'Em configuração',
            EM: 'Em manutenção',
            CM: 'Com mensagem',
            ON: 'Online'
         }
         return status[value]
      },
      corTexto(st_operacao) {
         const variant = {
            OF: 'text-secondary',
            EF: 'text-danger',
            EC: 'text-dark',
            EM: 'text-warning',
            ON: 'text-success'
         }
         return variant[st_operacao]
      },
      corVariant(st_operacao) {
         const variant = {
            OF: 'secondary',
            EF: 'danger',
            EC: 'dark',
            EM: 'warning',
            ON: 'success'
         }
         return variant[st_operacao]
      },
      verificaPressao() {
         const { CSP, P1, PMX, PMN, AL1 } = this.dadosLimpeza
         if (CSP == 0) {
            this.configuraPressao(4)
         } else if (this.verificarBitNaPosicao(AL1, 8)) {
            this.configuraPressao(0)
         } else if (P1 > PMX) {
            this.configuraPressao(2)
         } else if (P1 < PMN) {
            this.configuraPressao(1)
         } else {
            this.configuraPressao(3)
         }
      },
      verificaVibracao() {
         const { CSV, V1, VMX, VMN, AL1 } = this.dadosLimpeza
         if (CSV == 0) {
            this.configuraVibracao(4)
         } else if (this.verificarBitNaPosicao(AL1, 8)) {
            this.configuraVibracao(2)
         } else if (V1 > VMX) {
            this.configuraVibracao(2)
         } else if (V1 >= VMN && V1 <= VMX) {
            this.configuraVibracao(3)
         } else {
            this.configuraVibracao(3)
         }
      },
      verificaFiltroManga() {
         const { CPE, AC1, AL1 } = this.dadosLimpeza
         if (CPE == 0) {
            this.configuraFiltroManga(3)
         } else if (this.verificarBitNaPosicao(AC1, 4)) {
            this.configuraFiltroManga(1)
         } else if (!this.verificarBitNaPosicao(AC1, 4)) {
            this.configuraFiltroManga(0)
         } else if (this.verificarBitNaPosicao(AL1, 4)) {
            this.configuraFiltroManga(2)
         } else {
            this.configuraFiltroManga(0)
         }
      },
      verificaManutencao() {
         const { AL1 } = this.dadosLimpeza
         if (this.verificarBitNaPosicao(AL1, 6) || this.verificarBitNaPosicao(AL1, 7)) {
            this.configuraManutencao(true)
         } else {
            this.configuraManutencao(false)
         }
      },
      verificaVentilador() {
         const { AC1, AL1 } = this.dadosLimpeza
         if (this.verificarBitNaPosicao(AC1, 3)) {
            this.configuraVentilador(1)
         } else if (!this.verificarBitNaPosicao(AC1, 3)) {
            this.configuraVentilador(0)
         } else if (this.verificarBitNaPosicao(AL1, 3)) {
            this.configuraVentilador(2)
         } else {
            this.configuraVentilador(0)
         }
      },
      verificaPeneira() {
         const { AC1, AL1 } = this.dadosLimpeza
         if (this.verificarBitNaPosicao(AC1, 2)) {
            this.configuraMotorPeneira(1)
         } else if (!this.verificarBitNaPosicao(AC1, 2)) {
            this.configuraMotorPeneira(0)
         } else if (this.verificarBitNaPosicao(AL1, 2)) {
            this.configuraMotorPeneira(2)
         } else {
            this.configuraMotorPeneira(0)
         }
      },
      configuraPressao(EstadoSensorPressao) {
         //se estiver parado, ira mostrar o icone do sensor de pressao e define ele para cinza
         if (EstadoSensorPressao == 0) {
            document.getElementById('IconSensorDePressao').style.visibility = 'visible'
            document.getElementById('CirculoIconPressao').setAttribute('fill', 'grey')
         }
         //se estiver fora do range, ira mostrar o icone do sensor de pressao e define ele para azul
         else if (EstadoSensorPressao == 1) {
            document.getElementById('IconSensorDePressao').style.visibility = 'visible'
            document.getElementById('CirculoIconPressao').setAttribute('fill', 'blue')
         }
         //se estiver em erro, ira mostrar o icone do sensor de pressao e define ele para vermelho
         else if (EstadoSensorPressao == 2) {
            document.getElementById('IconSensorDePressao').style.visibility = 'visible'
            document.getElementById('CirculoIconPressao').setAttribute('fill', 'red')
         }
         //se estiver ligado, ira mostrar o icone do sensor de pressao e define ele para verde
         else if (EstadoSensorPressao == 3) {
            document.getElementById('IconSensorDePressao').style.visibility = 'visible'
            document.getElementById('CirculoIconPressao').setAttribute('fill', 'green')
         }
         //se nao possuir sensor de pressao, ira esconder o icone do sensor de pressao
         else if (EstadoSensorPressao == 4) {
            document.getElementById('IconSensorDePressao').style.visibility = 'hidden'
         }
      },
      configuraVibracao(EstadoSensorVibracao) {
         //verifica se sensor de vibracao existe, entao mostra icone de vibracao em cinza
         if (EstadoSensorVibracao == 0) {
            document.getElementById('IconSensorDeVibracao').style.visibility = 'visible'
            document.getElementById('CirculoIconVibracao').setAttribute('fill', 'grey')
         }
         //se estiver fora do range, mostra icone de vibracao em azul
         else if (EstadoSensorVibracao == 1) {
            document.getElementById('IconSensorDeVibracao').style.visibility = 'visible'
            document.getElementById('CirculoIconVibracao').setAttribute('fill', 'blue')
         }
         //se estiver em erro, mostra icone de vibracao em vermelho
         else if (EstadoSensorVibracao == 2) {
            document.getElementById('IconSensorDeVibracao').style.visibility = 'visible'
            document.getElementById('CirculoIconVibracao').setAttribute('fill', 'red')
         }
         //se estiver ligado, mostra o icone de vibracao em verde
         else if (EstadoSensorVibracao == 3) {
            document.getElementById('IconSensorDeVibracao').style.visibility = 'visible'
            document.getElementById('CirculoIconVibracao').setAttribute('fill', 'green')
         }
         //se nao possuir sensor de vibracao, esconde o icone de vibracao
         else if (EstadoSensorVibracao == 4) {
            document.getElementById('IconSensorDeVibracao').style.visibility = 'hidden'
         }
      },
      configuraManutencao(EstadoIconeDeManutencao) {
         //verifica se tem algo errado e exibe o icone de manutenção
         if (EstadoIconeDeManutencao == true) {
            document.getElementById('IconManutecao').style.visibility = 'visible'
         }
         //se nao tiver, esconde o icone de manutenção
         else {
            document.getElementById('IconManutecao').style.visibility = 'hidden'
         }
      },
      configuraFiltroManga(EstadoDoFiltroManga) {
         //se estiver parado, mostra o icone do ventilador parado e cinza o icone do filtro de manga
         if (EstadoDoFiltroManga == 0) {
            document.getElementById('PaVentiladorParado').style.visibility = 'visible'
            document.getElementById('PaVentiladorGirando').style.visibility = 'hidden'
            document.getElementById('CirculoFiltroManga').setAttribute('fill', 'grey')
         }
         //se estiver ligado, mostra o icone do ventilador girando e verde o icone do filtro de manga
         else if (EstadoDoFiltroManga == 1) {
            document.getElementById('PaVentiladorGirando').style.visibility = 'visible'
            document.getElementById('PaVentiladorParado').style.visibility = 'hidden'
            document.getElementById('CirculoFiltroManga').setAttribute('fill', 'green')
         }
         //se estiver em erro, mostra o icone do ventilador parado e vermelho o icone do filtro de manga
         else if (EstadoDoFiltroManga == 2) {
            document.getElementById('PaVentiladorGirando').style.visibility = 'hidden'
            document.getElementById('PaVentiladorParado').style.visibility = 'visible'
            document.getElementById('CirculoFiltroManga').setAttribute('fill', 'red')
         }
         //se nao possuir filtro de manga, esconde o icone do filtro de manga
         else if (EstadoDoFiltroManga == 3) {
            document.getElementById('FiltroDeManga').style.visibility = 'hidden'
         }
      },
      configuraVentilador(EstadoDoVentilador) {
         //se estiver parado, mostra o ventilador parado e define a cor cinza para o icone
         if (EstadoDoVentilador == 0) {
            document.getElementById('PaVentiladorParado1').style.visibility = 'visible'
            document.getElementById('PaVentiladorGirando1').style.visibility = 'hidden'
            document.getElementById('CirculoVentilador').setAttribute('fill', 'grey')
         }
         //se estiver ligado, mostra o ventilador girando e define a cor verde para o icone
         else if (EstadoDoVentilador == 1) {
            document.getElementById('PaVentiladorGirando1').style.visibility = 'visible'
            document.getElementById('PaVentiladorParado1').style.visibility = 'hidden'
            document.getElementById('CirculoVentilador').setAttribute('fill', 'green')
         }
         //se estiver em erro, mostra o ventilador parado e define a cor vermelha para o icone
         else if (EstadoDoVentilador == 2) {
            document.getElementById('PaVentiladorGirando1').style.visibility = 'hidden'
            document.getElementById('PaVentiladorParado1').style.visibility = 'visible'
            document.getElementById('CirculoVentilador').setAttribute('fill', 'red')
         }
      },
      configuraMotorPeneira(EstadoMotorPeneira) {
         //se estiver parado, mostra o icone do motor na cor cinza
         if (EstadoMotorPeneira == 0) {
            document.getElementById('CirculoIconPeneira').setAttribute('fill', 'grey')
         }
         //se estiver ligado, mostra o icone do motor na cor verde
         else if (EstadoMotorPeneira == 1) {
            document.getElementById('CirculoIconPeneira').setAttribute('fill', 'green')
         }
         //se estiver em erro, mostra o icone do motor na cor vermelha
         else if (EstadoMotorPeneira == 2) {
            document.getElementById('CirculoIconPeneira').setAttribute('fill', 'red')
         }
      },
      posicaoPeneira(ModeloMaquina) {
         //se a maquina for do modelo malt60, mostra apenas uma peneira
         if (ModeloMaquina == 0) {
            document.getElementById('MaquinaInteira').style.visibility = 'hidden'
            document.getElementById('IconSensorDePressao').style.visibility = 'hidden'
            document.getElementById('IconManutecao').style.visibility = 'hidden'
            document.getElementById('IconSensorDeVibracao').style.visibility = 'hidden'
            document.getElementById('Piso').style.visibility = 'hidden'
         }
         if (ModeloMaquina == 1) {
            document.getElementById('MaquinaLimpeza').style.visibility = 'visible'
            document.getElementById('ParteInferriorDaMaquina').style.visibility = 'visible'
            document.getElementById('Peneira1').style.visibility = 'hidden'
            document.getElementById('Peneira2').style.visibility = 'hidden'
            document.getElementById('Peneira3').style.visibility = 'hidden'
            document.getElementById('ParteInferriorDaMaquina').setAttribute('transform', 'translate(0, -5325)')
            document.getElementById('Ventilador').style.visibility = 'visible'
            document.getElementById('Piso').setAttribute('transform', 'translate(0, -4325)')
         }
         //se a maquina for do modelo malt120, mostra duas peneiras
         else if (ModeloMaquina == 2) {
            document.getElementById('MaquinaLimpeza').style.visibility = 'visible'
            document.getElementById('ParteInferriorDaMaquina').style.visibility = 'visible'
            document.getElementById('Peneira1').style.visibility = 'visible'
            document.getElementById('Peneira2').style.visibility = 'hidden'
            document.getElementById('Peneira3').style.visibility = 'hidden'
            document.getElementById('ParteInferriorDaMaquina').setAttribute('transform', 'translate(0, -3565)')
            document.getElementById('Peneira1').setAttribute('transform', 'translate(0, -3565)')
            document.getElementById('Piso').setAttribute('transform', 'translate(0, -2565)')
         }
         //se a maquina for no modelo malt180, mostra tres peneiras
         else if (ModeloMaquina == 3) {
            document.getElementById('MaquinaLimpeza').style.visibility = 'visible'
            document.getElementById('ParteInferriorDaMaquina').style.visibility = 'visible'
            document.getElementById('Peneira1').style.visibility = 'visible'
            document.getElementById('Peneira2').style.visibility = 'hidden'
            document.getElementById('Peneira3').style.visibility = 'visible'
            document.getElementById('ParteInferriorDaMaquina').setAttribute('transform', 'translate(0, -1765)')
            document.getElementById('Peneira1').setAttribute('transform', 'translate(0, -1765)')
            document.getElementById('Peneira3').setAttribute('transform', 'translate(0, -1765)')
            document.getElementById('Piso').setAttribute('transform', 'translate(0, -765)')
         }
         //se a maquina for no modelo malt240, mostra quatro peneiras
         else if (ModeloMaquina == 4) {
            document.getElementById('MaquinaLimpeza').style.visibility = 'visible'
            document.getElementById('ParteInferriorDaMaquina').style.visibility = 'visible'
            document.getElementById('Peneira1').style.visibility = 'visible'
            document.getElementById('Peneira2').style.visibility = 'visible'
            document.getElementById('Peneira3').style.visibility = 'visible'
         }
      },
      toggleFullScreen() {
         const elem = this.$refs.abasContainer
         this.fullscreen = true

         if (!this.fullscreenElement) {
            if (elem.requestFullscreen) {
               elem.requestFullscreen()
            } else if (elem.mozRequestFullScreen) {
               elem.mozRequestFullScreen()
            } else if (elem.webkitRequestFullscreen) {
               elem.webkitRequestFullscreen()
            } else if (elem.msRequestFullscreen) {
               elem.msRequestFullscreen()
            }

            elem.style.overflow = 'auto'
            this.fullscreenElement = elem
         } else {
            if (document.exitFullscreen) {
               document.exitFullscreen()
            } else if (document.mozCancelFullScreen) {
               document.mozCancelFullScreen()
            } else if (document.webkitExitFullscreen) {
               document.webkitExitFullscreen()
            } else if (document.msExitFullscreen) {
               document.msExitFullscreen()
            }

            this.fullscreenElement = null
            this.fullscreen = false
         }
      },
      exitFullScreen() {
         if (document.exitFullscreen) {
            document.exitFullscreen()
         } else if (document.mozCancelFullScreen) {
            document.mozCancelFullScreen()
         } else if (document.webkitExitFullscreen) {
            document.webkitExitFullscreen()
         } else if (document.msExitFullscreen) {
            document.msExitFullscreen()
         }

         this.fullscreenElement = null
         this.fullscreen = false
      }
   },

   mounted() {
      const idLimpezaLocalStorage = localStorage.getItem('lim')
      if (idLimpezaLocalStorage && idLimpezaLocalStorage != 'undefined') {
         this.idLimpeza = idLimpezaLocalStorage
      } else {
         this.idLimpeza = this.$route.params.idLimpeza
         localStorage.setItem('lim', this.idLimpeza)
      }
      this.buscarDados()
      this.buscarEquipamento()
      // this.posicaoPeneira(2)
      // this.configuraPressao(1)
      // this.configuraVibracao(3)
      // this.configuraFiltroManga(0)
      // this.configuraManutencao(true)
      // this.configuraVentilador(1)
      // this.configuraMotorPeneira(2)
   },

   watch: {
      dadosLimpeza(newValue) {
         if (newValue) {
            this.posicaoPeneira(this.dadosLimpeza?.MOD)
            this.verificaPressao()
            this.verificaVibracao()
            this.verificaFiltroManga()
            this.verificaManutencao()
            this.verificaVentilador()
            this.verificaPeneira()
         }
      }
   }
}
</script>

